import React, { useCallback, useEffect, useState } from 'react'
import { Col, Form, Row, Table } from 'react-bootstrap'
import './home.css';
import ExportExcel from '../components/ExportExcel';
import DownloadModal from '../components/DownloadModal';

function Home() {
    const [data, setData] = useState("");
    const [home, setHome] = useState("");
    const [purposeOfVisit, setPurposeOfVisit] = useState("");
    const [modeOfConveyance, setModeOfConveyance] = useState("");
    const [vehicleNumber, setVehicleNumber] = useState("");
    const [formattedData, setFormattedData] = useState([]);

    const handleChange = useCallback(() => {
        console.log(data);
        const rows = data.split('@');
        console.log(rows);

        let arr = [];
        rows.map((item) => {
            const places = item.slice(0, item.indexOf("/") - 2);
            const date = item.slice(item.indexOf("/") - 2, item.indexOf("/") + 8);
            const startTime = item.slice(item.indexOf(":") - 2, item.indexOf(":") + 3);
            const endTime = item.slice(item.lastIndexOf(":") - 2, item.lastIndexOf(":") + 3);

            arr = [...arr, { places: places ? places.split(",") : [], date, startTime, endTime }];
        })

        setFormattedData(arr);
        console.log(arr);
    }, [data])

    useEffect(() => {
        handleChange()
    }, [data])

    return (
        <>
            <div className='container'>
                <h1>Expense Sheet Generator</h1>

                <Form>
                    <Row>
                        <Form.Group as={Col} className="mb-3">
                            <Form.Label>Home</Form.Label>
                            <Form.Control type="text" placeholder="Enter home" value={home} onChange={(e) => setHome(e.target.value)} />
                        </Form.Group>
                        <Form.Group as={Col} className="mb-3">
                            <Form.Label>Purpose of visit</Form.Label>
                            <Form.Control type="text" placeholder="Enter email" value={purposeOfVisit} onChange={(e) => setPurposeOfVisit(e.target.value)} />
                        </Form.Group>
                        <Form.Group as={Col} className="mb-3">
                            <Form.Label>Mode</Form.Label>
                            <Form.Control type="text" placeholder="Enter Mode" value={modeOfConveyance} onChange={(e) => setModeOfConveyance(e.target.value)} />
                        </Form.Group>
                        <Form.Group as={Col} className="mb-3">
                            <Form.Label>Vehicle Number</Form.Label>
                            <Form.Control type="text" placeholder="Enter vehicle number" value={vehicleNumber} onChange={(e) => setVehicleNumber(e.target.value)} />
                        </Form.Group>
                    </Row>
                    <Form.Group className="mb-3">
                        <Form.Label>Data</Form.Label>
                        <Form.Control as="textarea" placeholder="Enter data" value={data} onChange={(e) => setData(e.target.value)} />
                    </Form.Group>
                </Form>
                <h2 id='result'>Result</h2>
                <ExportExcel excelData={[]} fileName='Excel Export' className="my-3" />
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Date</th>
                            <th>Purpose Of Visit</th>
                            <th>Mode Of Conveyance</th>
                            <th>Vehicle Number</th>
                            <th>From</th>
                            <th>Time</th>
                            <th>To</th>
                            <th>Time</th>
                        </tr>
                    </thead>
                    <tbody>
                        {formattedData.map((record, index) => {
                            return (
                                <>
                                    {record.places.length ? record.places.map((place, rIndex) => {
                                        return (
                                            <tr key={`${index}${rIndex}`}>
                                                <td>{record.date}</td>
                                                <td>{purposeOfVisit}</td>
                                                <td>{modeOfConveyance}</td>
                                                <td>{vehicleNumber}</td>

                                                <td>{rIndex === 0 ? home : place}</td>
                                                <td>{rIndex === 0 && record.startTime}</td>
                                                <td>{record.places.length - 1 === rIndex ? home : record.places[rIndex + 1]}</td>
                                                <td>{record.places.length - 1 === rIndex && record.endTime}</td>
                                            </tr>
                                        )
                                    }) : null}
                                </>
                            )
                        })}
                    </tbody>
                </Table>
            </div>
            <DownloadModal show={showDownloadModal}/>
        </>
    )
}

export default Home
